nox.ext.apps.vmanui.util.arrayUnion = function() {
    var o = {}, a = [];

    dojo.forEach(arguments, function(arga) {
        dojo.forEach(arga, function(item) {
            o[item] = true;
        });
    });

    for (var item in o) {
        a.push(item);
    }

    o = null;

    return a;
};

nox.ext.apps.vmanui.util.arrayIntersect = function() {
    var a = [];

    if (arguments.length > 1) {
        var a0 = arguments[0];
        var orig_arguments = arguments;

        dojo.forEach(a0, function(item) {
            var bMissing = false;

            for (var i = 1; i < orig_arguments.length; i++) {
                var an = orig_arguments[i];

                if (dojo.indexOf(an, item) == -1) {
                    bMissing = true;
                }
            };

            if (! bMissing) {
                a.push(item);
            }
        });
    } else {
        return arguments[0];
    }

    return a;
};

nox.ext.apps.vmanui.util.keys = function(o) {
    if (dojo.isObject(o)) {
        var keys = [];

        for (var key in o) {
            keys.push(key);
        }

        return keys;
    }

    throw Error("Argument to keys() is not an object");
};

nox.ext.apps.vmanui.util.entityReference = function(sEntityName) {
    var e = dojo.doc.createElement('div');
    e.innerHTML = '&' + sEntityName + ';';
    return e.innerHTML;
};

nox.ext.apps.vmanui.util.toLowerFirst = function(sString) {
    return sString.replace(/^([A-Za-z])/, function(sMatch) {
        return sMatch.toLowerCase();
    });
};

nox.ext.apps.vmanui.util.toUpperFirst = function(sString) {
    return sString.replace(/^([A-Za-z])/, function(sMatch) {
        return sMatch.toUpperCase();
    });
};

nox.ext.apps.vmanui.util.toTitleCase = function(sString) {
    return sString.replace(/\b([A-Za-z])/g, function(sMatch) {
        return sMatch.toUpperCase();
    });
};

nox.ext.apps.vmanui.util.escapeMarkup = function(sOld) {
    var sNew = sOld;
    sNew = sNew.replace(/&/g, '&amp;');
    sNew = sNew.replace(/</g, '&lt;');
    sNew = sNew.replace(/>/g, '&gt;');
    return sNew;
};

nox.ext.apps.vmanui.util.unescapeMarkup = function(sOld) {
    var sNew = sOld;
    sNew = sNew.replace(/&lt;/g, '<');
    sNew = sNew.replace(/&gt;/g, '>');
    sNew = sNew.replace(/&amp;/g, '&');
    return sNew;
};

/**
 * Returns the first ancestor node found that has the 
 * class ancestorClass
 */
nox.ext.apps.vmanui.util.findAncestorNodeByClass = function(domNode, ancestorClass) {
    var ancestorNode = domNode.parentNode;
    if (ancestorNode && dojo.hasClass(ancestorNode, ancestorClass)) {
        return ancestorNode;
    }
    return ancestorNode && this.findAncestorNodeByClass(ancestorNode, ancestorClass);
};

/**
 * JS port of similar function in status_util.mako
 * Ellipsize a long string if it's the only token
 * in the expression (work around a FF bug) 
 */
nox.ext.apps.vmanui.util.cond_ellipsis = function(str, max_word_len) {
    if (! dojo.isString(str)) {
        return '';
    }
    str = str || '';
    if (! str) {
        return '';
    }
    max_word_len = (max_word_len == null) ? 60 : max_word_len;
    var tokens = str.split(/\s+/);
    if (tokens.length > 1) {
        return str;
    }
    str = str.substr(0, max_word_len) + '...';
    return str;
};

