(function() {
    var plotter = nox.ext.apps.vmanui.plotUtil;

    plotter.plotNetFlow = function(chartId, seriesData, labelData, plotOrder, theme) {
        dojo.byId(chartId).innerHTML = '';

        var vertAxis = nox.ext.apps.vmanui.RangeAreaVerticalAxis;
        if (theme == "dashboard") {
            vertAxis = nox.ext.apps.vmanui.DashboardRangeAreaVerticalAxis;
        }

        var oPlotParams = {
            type    : nox.ext.apps.vmanui.RangeArea,
            tension : "X"
        };

        var chart = new dojox.charting.Chart2D(chartId)
            .addPlot("default", oPlotParams)
            .addAxis("x", {
                htmlLabels  : true,
                labels      : labelData
            })
            .addAxis("y", {
                type            : vertAxis,
                vertical        : true,
                htmlLabels      : true,
                maxLabelSize    : 40,
                labelFunc       : function(label, num, precision) {
                    // This function labels negative values as positive.
                    // It also formats the label using dojo's default
                    // formatting pattern so that labels are localized
                    // and have sensible precision.
                    return dojo.number.format(Math.abs(num));
                }
            });

        if (theme == "dashboard")
            chart.setTheme(nox.ext.apps.vmanui.charting.themes.niciraDashboard)
        else
            chart.setTheme(nox.ext.apps.vmanui.charting.themes.nicira)

        plotter.setChartSeries(chart, plotOrder, seriesData, labelData);

        return chart;
    };

    plotter.setChartSeries = function(chart, plotOrder, seriesData, labelData) {
        for (var name in chart.runs) {
            chart.removeSeries(name);
        }
        // The x-axis is time which is always moving forward in the graph, so
        // we need to update the label data to get the latest timestamp labels,
        // chart.addAxis will destroy the existing axis metadata before adding
        // the newly specified axis.
        chart.addAxis("x", { htmlLabels : true, labels : labelData });

        try {
            if (plotOrder) {
                dojo.forEach(plotOrder.reverse(), function(p) {
                    if (seriesData[p]) {
                        chart.addSeries(p, seriesData[p]["data"]);
                    }
                });
            } else {
                for (p in seriesData) {
                    chart.addSeries(p, seriesData[p]["data"]);
                }
            }

            chart.render();
        } catch(e) {
            console.log("Render Error:", e);
        }
    };

    plotter.fillTableLegendColors = function(chart, aRows) {
        var oColorMap = {};
        for (var i = 0; i < chart.series.length; i++) {
            var oRun = chart.series[i];
            oColorMap[ oRun.name ] = oRun.dyn.fill.toHex().toUpperCase();
        }

        for (var i = 0; i < aRows.length; i++) {
            var oRow = aRows[i];

            var sId = oRow.series_id;

            if (sId && oColorMap[sId]) {
                oRow.fillLegendColor(oColorMap[sId]);
            }
        }
    }
})();

