    var vis_module        = vmanui.vac_visibility;

    dashboard_module.charts         = {};
    dashboard_module.chartRotation  = 0;
    dashboard_module.chartsNeeded   = [];

    dashboard_module.chart_metadata = [
        {
            plotId      : 'dashboardBitsPlot',
            binType     : 'host',
            countType   : 'bits'
        },
        {
            plotId      : 'dashboardFlowsPlot',
            binType     : 'host',
            countType   : 'flows'
        },
        {
            plotId      : 'dashboardPacketsPlot',
            binType     : 'host',
            countType   : 'packets'
        }
    ];

    dashboard_module.dashboard_init = function(new_url, from_click) {
        nox.ext.apps.vmanui.main.set_document_subtitle('Dashboard');

        dashboard_module.update_server_stats();
        dashboard_module.update_network_stats();
        dashboard_module.update_network_events(from_click);
        dashboard_module.update_admin_events(from_click);

        var dm = dashboard_module;
        // Because vac is loaded after dashboard, the above definition
        // of vis_module may not have been defined yet
        if (vis_module == undefined) {
            vis_module = vmanui.vac_visibility;
        }

        // Check for plots that haven't been generated before
        dashboard_module.chartsNeeded = [];

        dojo.forEach(dashboard_module.chart_metadata, function(oChartMeta) {
            var sPlotId = oChartMeta.plotId;

            if (! dashboard_module.charts[sPlotId]) {
                dashboard_module.chartsNeeded.push(oChartMeta);
            }
        });

        // If none of the charts *needs* to be generated, then we queue up
        // the next one in the rotation
        if (dashboard_module.chartsNeeded.length == 0) {
            var iChartCount = dashboard_module.chart_metadata.length;

            dashboard_module.chartRotation =
                (dashboard_module.chartRotation + 1) % iChartCount;

            var oChartMeta = dashboard_module.chart_metadata[ dashboard_module.chartRotation ];
            dashboard_module.chartsNeeded.push(oChartMeta);
        }

        if (dashboard_module.chartsNeeded.length) {
            if (dojo.isIE) {
                main_module.pause_clicked(false);
            }
        }

        dashboard_module.create_needed_chart();

        return false;
    };

    dashboard_module.create_needed_chart = function() {
        if (dashboard_module.chartsNeeded.length) {
            var oChartMeta = dashboard_module.chartsNeeded.shift();

            dashboard_module._update_dashboard_plot(
                oChartMeta.plotId, oChartMeta.binType, oChartMeta.countType
            );
        } else {
            if (dojo.isIE) {
                nox.ext.apps.vmanui.main.play_clicked(false);
            }
        }
    };

    dashboard_module.update_server_stats = function() {
        dashboard_module.update_server_uptime();
        dashboard_module.update_server_cpu_load();
    };

