Date.prototype.strftime = function(format) {
    function sub_fn(c,month,day,hours,minutes,d) {
      function pad(num) {
          return dojo.string.pad(num, 2, "0");
      }
      switch(c) {
        case 'a': return ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"][day];
        case 'A': return ["Sunday", "Monday", "Tuesday", "Wednesday" ,"Thursday", "Friday", "Saturday"][day];
        case 'b': return ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"][month];
        case 'B': return ["January", "February", "March", "April", "May", "June", "July", "August",
                            "September", "October", "November", "December"][month];
        case 'c': return d.toString();
        case 'd': return d.getDate();
        case 'D': return pad(d.getDate());
        case 'H': return pad(hours);
        case 'i': return (hours === 12 || hours === 0) ? 12 : (hours + 12) % 12;
        case 'I': return pad((hours === 12 || hours === 0) ? 12 : (hours + 12) % 12);
        case 'm': return pad(month + 1);
        case 'M': return pad(minutes);
        case 'p': return hours > 11 ? 'PM' : 'AM';
        case 'S': return pad(d.getSeconds());
        case 'w': return day;
        case 'y': return pad(d.getFullYear() % 100);
        case 'Y': return d.getFullYear().toString();
        case 'Z':
            var offset = d.getTimezoneOffset() / 60 * -100;
            var sign   = offset / Math.abs(offset) < 0 ? '-' : '+';
            return sign + dojo.string.pad(Math.abs(offset), 4, '0');
        case 'z': return dojo.date.getTimezoneName(d);
        default: return c;
      }
    }
    var day = this.getDay(), month = this.getMonth();
    var hours = this.getHours(), minutes = this.getMinutes();

    var new_str = "";
    var len = format.length;
    for(var i = 0; i < len; i++) {
      var c = format.charAt(i);
      if(c == '%' && (++i) < len) {
        new_str += sub_fn(format.charAt(i),month,day,hours,minutes,this);
      } else {
        new_str += c;
      }
    }
    return new_str;
};


