    settings_module.verify_password = function(sPassword, sConfirmPassword) {
        if (sPassword != sConfirmPassword) {
            return "Passwords don't match";
        } else if (! sPassword.length) {
            return "Password cannot be empty string";
        }

        if (sPassword.length > settings_module.MAX_NAME_LENGTH) {
            return "Passwords must contain " + settings_module.MAX_NAME_LENGTH +
                    " or fewer characters"
        }

        return '';
    };

    settings_module.verify_username = function(sName) {
        if (sName == "") {
            return "Empty user names not allowed"
        }

        if (/\s/.test(sName)) {
            return "User names cannot contain whitespace"
        }

        if (sName.length > settings_module.MAX_NAME_LENGTH) {
            return "User names must contain " + settings_module.MAX_NAME_LENGTH +
                    " or fewer characters"
        }

        return '';
    };

    settings_module.submit_add_user = function() {
        var oName       = dijit.byId("new_user");
        var oPriv       = dijit.byId("priv");
        var oCred1      = dijit.byId("new_user_cred1");
        var oCred2      = dijit.byId("new_user_cred2");
        var oCsrfToken  = dijit.byId("csrf_token_s");

        if (
            ! (oName.isValid() && oPriv.isValid() &&
            oCred1.isValid() && oCred2.isValid() )
        ) {
            return;
        }

        dijit.byId("add_user_dialog").hide();

        var name        = nox.ext.apps.vmanui.util.escapeMarkup(oName.attr('value'));
        var priv        = oPriv.attr('value');
        var cred1       = oCred1.attr('value');
        var cred2       = oCred2.attr('value');
        var csrfToken   = oCsrfToken.attr('value');

        var sPassErr = settings_module.verify_password(cred1, cred2);
        var sUserErr = settings_module.verify_username(name);

        if (sUserErr) {
            settings_module.cancel_user_add();
            vmanui.main.vmanui_error(sUserErr);
            return;
        }

        if (sPassErr) {
            settings_module.cancel_user_add();
            vmanui.main.vmanui_error(sPassErr);
            return;
        }

        nox.ext.apps.vmanui.rawXhrPost( {
            url         : "ws.v1/adminuser",
            headers     : { "content-type": "application/json",
                            "X-CSRF-Token": csrfToken },
            timeout     : 30000,
            postData    : dojo.toJson({
                "name"          : name,
                "priv"          : priv,
                "cred"          : cred1,
                "csrf_token"    : csrfToken
            }),
            // why not just "load : settings_module.refresh_settings_section"?
            load        : function (response, ioArgs) {
                settings_module.refresh_settings_section();
            },
            error       : function(response, ioArgs) {
                settings_module.cancel_user_add();
                vmanui.main.vmanui_error("Failed to add user '" + name + "'");
            }
        });
    };

