    // NOTE: a shutdown effectively restarts vnetmanager,
    // as the virtex-monitor will restart the process
    settings_module.restart_vnetmanager = function() {
        // Disable connection polling immediately
        vmanui.main.disable_connection_testing();

        var csrf_token = dijit.byId("csrf_token_s").getValue();
        nox.ext.apps.vmanui.rawXhrPut( {
            url         : "/ws.v1/nox/local_config/shutdown",
            headers     : { "content-type" : "application/json",
                            "X-CSRF-Token" : csrf_token },
            putData     : dojo.toJson("process"),
            timeout     : 30000,
            handleAs    : "json",
            load        : dojo.hitch(this, function(response) {
                settings_module.show_any_message_dialog(
                    "Please wait while the " + vmanui.main.app_name_short +
                    " UI restarts.", null,
                    "Restarting " + vmanui.main.app_name_short
                );

                // don't start polling right away, or we may not
                // having shutdown quite yet
                setTimeout(function() {
                    settings_module.restart_poll(15, 500, false, null);
                }, 2000);
            }),
            error       : function() {
                vmanui_error("Failure while requesting a " + vmanui.main.app_name_short + " restart.");
            }
        });
    };

    // this assumes the caller has placed a useful message in the
    // 'any_message_dialog' after performing an operation that will require nox
    // to restart.  This code recursive calls itself 'count' times before giving
    // up and showing an error.  It queries a simple 'is nox up' webservice every
    // 'wait' milliseconds to first confirm that nox is down, and then confirm
    // that it is back up.  Not fool-proof, but it seems to do the job.
    // 'was_down' is used to indicate whether we have already seen nox go down.
    // Those calling restart_poll should always set the value to false
    // 'on_error' is called if we conclude that nox is permenently down
    settings_module.restart_poll = function(count, wait, was_down, on_error) {
        if (count === 0) {
            settings_module.hide_any_message_dialog();

            if (on_error) {
                on_error();
            } else {
                // Once the user clicks OK to the error dialog, we want
                // to restart the master connection polling, so we supply
                // a function reference to the vmanui_error() method; it
                // will get connected to the dialog's hide() method
                vmanui.main.vmanui_error(
                    "Unable to restart " + vmanui.main.app_name_short,
                    function() { window.location.reload(); }
                );
            }

            if (! was_down) {
                vmanui.main.reenable_connection_testing();
            }

            return;
        }
        dojo.xhrGet({
            url             : "/ws.v1/nox/up",
            timeout         : 3000,
            preventCache    : true,
            load            : function(response) {
                // this shouldn't happen, unless we are not authenticating
                // admin users
                if (was_down) {
                    // just refresh the current page
                    settings_module.hide_any_message_dialog();
                    window.location.reload();
                } else {
                    setTimeout(function() {
                        settings_module.restart_poll(count - 1, wait, false, on_error);
                    }, wait);
                }
            },
            error           : function(response) {
                if (response.status == 401) {
                    settings_module.hide_any_message_dialog();
                    window.location = "/login?last_page=/";
                } else {
                    setTimeout(function() {
                        settings_module.restart_poll(count - 1, wait, true, on_error);
                    }, wait);
                }
            }
        });
    };


