        dijit.byId("add_to_admin_group").attr('disabled',true);

        // These two listeners enable/disable the 'Add' button based on whether the
        // filtering select contains something valid.
        dojo.connect(fs, "onChange", function() {
            dijit.byId("add_to_admin_group").attr('disabled', !fs.isValid());
        });
