(function () {
  var regex = nox.ext.apps.vmanui.regex;

    // NOTE: please write all regexes so they can be safely composed without adding
    // additional parentheses

    // any number from 1 to 65535
    regex.transport_port = "(([1-9]\\d?\\d?\\d?)|([1-5]\\d{4})|(6[0-4]\\d{3})|(65[0-4]\\d{2})|(655|[0-2]\\d)|(6553[0-5]))";

    // letters, digits, hyphen
    regex.LDH = "([a-zA-Z0-9\\-]+)";

    // does not actually check that we end in a valid tld
    regex.dns_name = "(" + regex.LDH + "(\\." + regex.LDH + ")*)";

    // enforce at least 2 (dot-separated) fields in the name
    regex.dns_domain = "(" + regex.LDH + "(\\." + regex.LDH + ")+)";

    // enforce at least 2 (dot-separated) fields in the name
    regex.dns_domain_list = "(" + regex.dns_domain + "(\\s*,\\s*" + regex.dns_domain  + ")*)";

    // any number from 0 to 255
    regex.decimal_byte = "((\\d\\d?)|([01]\\d{2})|(2[0-4]\\d)|(25[0-5]))";

    regex.ipv4_address = "((" + regex.decimal_byte + "\\.){3}" + regex.decimal_byte + ")";

    regex.ipv4_net_suffix = "(/((\\d)|([1-2]\\d)|(3[0-2])))";

    regex.ipv4_net = regex.ipv4_address + regex.ipv4_net_suffix;

    regex.ipv4_net_or_address = "(" + regex.ipv4_address + regex.ipv4_net_suffix + "?)";

    regex.ipv4_net_or_address_list = "(\\s*"+regex.ipv4_net_or_address+"([,\\s]+" + regex.ipv4_net_or_address + ")*\\s*?)";

    regex.dns_or_ipv4 = "(" + regex.dns_name + "|" + regex.ipv4_address + ")";

    regex.host_and_port = "(" + regex.dns_or_ipv4 + ":" + regex.transport_port + ")";

    regex.ip_and_port = "(" + regex.ipv4_address + ":" + regex.transport_port + ")";

    regex.ipv4_address_list = "(" + regex.ipv4_address + "(\\s*,\\s*" + regex.ipv4_address  + ")*)";

    // case-insensitive hex number 0 - ff , does not require zero padding
    regex.mac_address_octet = "([0-9a-fA-F]{1,2})";

    regex.mac_address =  "((" + regex.mac_address_octet + ":){5}" + regex.mac_address_octet + ")";

    // decimal number 0 to 4095
    regex.vlan = "((\\d\\d?\\d?)|([1-3]\\d{2})|(40[0-9][0-5]))";

    // 2-byte hex digit (case insensitive)
    regex.ethertype = "(0x([0-9a-zA-Z]){4})";
})();

