            var policy_node = byId("policy_div", content);
            if( ! policy_node ) {
                return; // happens normally if a non-policy tree node is selected
            }

            dojo.addClass(policy_node, this.read_only ? "policy_readonly" : "policy_edit");
            var role_uid = byId("role_uid", content).value;
            if (! this.ui_state[role_uid]) {
                this.ui_state[role_uid] = {
                    visibility_state  : {},
                    role_to_show      : {}
                };
            }

            if (this.pm) {
                this.pm.cancel();
            }
            this.lastPm = this.pm;
