dojo.declare("nox.ext.apps.vmanui.Search", [dijit._Widget, dijit._Templated], {
    
    templateString:"<form class=\"noxSearch\" dojoAttachPoint=\"containerNode\" dojoAttachEvent=\"onreset: onReset, onsubmit: onSubmit\"\n        >\n    <div class=\"searchInput\">\n        <img src=\"${imageBasePathStr}/searchIcon.gif\").toString()} alt=\"Filter your Search\" class=\"searchIcon\"\n            dojoAttachPoint=\"changeFilterNode\" />\n        <input type=\"text\" dojoType=\"dijit.form.TextBox\" dojoAttachPoint=\"searchString\" />\n        <img src=\"${imageBasePathStr}/searchClearIcon.gif\" alt=\"Clear your Search\" class=\"searchClear\"\n            dojoAttachEvent=\"onclick: onReset\" />\n    </div>\n</form>\n",

    imageBasePathStr: dojo.moduleUrl("nox.ext.apps.vmanui", "images").toString(),

    widgetsInTemplate: true,
    
    // standard construction/destruction methods
    
    _fillContent: function(/* Node */ source) {
        this._source = source;
    },

    startup: function() {
        if (this._started) { return; }
        this.reflectState(true);
        
        this.events = [
            dojo.connect(this.searchString, "onFocus", this, "onSearchFocus"),
            dojo.connect(this.searchString, "onBlur",  this, "onSearchBlur")
        ];
        var container_list = dojo.query(".searchContainer");
        if (container_list.length > 0) {
          this.container = dijit.getEnclosingWidget(container_list[0]);  
          this.recomputeWidth(); 
          if (this.container.resize ) { 
            dojo.connect(this.container,"resize", this, "recomputeWidth"); 
          }
        }
        this.inherited(arguments);
    },

    recomputeWidth : function() { 
        try { 
            var new_width = dojo.style(this.container.domNode,"width") - 100;
            this.searchString._setStyleAttr("width : " + new_width + "px");  
        } catch(e) { 
          nox.ext.apps.vmanui.main.console_log("Error resizing search box: ", e); 
        } 
    }, 

    destroyDescendants: function() {
        dojo.forEach(this.events, dojo.disconnect);
        this.inherited(arguments);
    },
    
    // business logic
    
    reflectState: function(/* Boolean */ empty) {
        this.isEmpty = empty;
        if (empty) {
            // We need to set a timeout.  Otherwise, the "empty" class sets
            // the searchClear image to display:none, which actually prevents
            // the onclick event from firing, in which case onReset() never
            // executes.
            setTimeout( function() {
                dojo.addClass(
                    dijit.byId("searchBox").containerNode,
                    "empty"
                );
            }, 100 );
        } else {
            dojo.removeClass(this.domNode, "empty");
            this.searchString.setValue("");
        }
    },
    
    onSearchFocus: function() {
        if (this.isEmpty) {
            this.reflectState(false);
        }
    },
    
    onSearchBlur: function() {
        if (this.searchString.getValue()) {
            this.isEmpty = false;
        } else {
            this.reflectState(true);
        }
    },
    
    onReset: function() {
        this.searchString.setValue("");  
        this.reflectState(true);
        this.submit(); 
    },
    
    onSubmit: function(evt) {
        dojo.stopEvent(evt); 
        // fill out internal inputs
        if (this.isEmpty) {
            this.searchString.setValue("");
        }
        this.submit(); 
        return false;
    }, 
 
    submit : function() { 
        if (!nox.ext.apps.vmanui.main.check_warn_on_nav())  {  
                return; // ignore click
        }
        // clear existing tree selection and reload tree using search term
        nox.ext.apps.vmanui.main.current_tree_item = null; 
        nox.ext.apps.vmanui.vac.load_nav_tree();
    }
});

