if(!dojo._hasResource["nox.ext.apps.vmanui.dialog.pool_connect_status"]){ //_hasResource checks added by build. Do not use _hasResource directly in your code.
dojo._hasResource["nox.ext.apps.vmanui.dialog.pool_connect_status"] = true;
dojo.provide("nox.ext.apps.vmanui.dialog.pool_connect_status");



dojo.declare(
    'nox.ext.apps.vmanui.dialog.pool_connect_status',
    [dijit._Widget, dojox.dtl._Templated  ],
    {
        popup : null,
        widgetsInTemplate: true,

        canceled : false,
        pool_obj : null,
        master_addr : "<unknown>",

        templateString:"<div dojoAttachPoint=\"containerNode\" style=\"width:300px\">\n  <div class=\"pollingDiv\">\n    <div>Trying to connect to '{{ master_addr }}'</div> \n    <div style=\"text-align:center;margin-top:12px;\">\n      <div dojoType=\"dijit.ProgressBar\" style=\"width:300px\" indeterminate=\"true\"></div>\n    </div> \n    <div class=\"dialogButtonContainer\">\n      <button dojoType=\"dijit.form.Button\" dojoAttachEvent=\"onClick:onCancel\">Cancel </button> \n    </div> \n  </div>\n  <div class=\"stealingDiv\">\n    <div style=\"margin-bottom:15px\">This pool is already managed by controller <span class=\"currentControllerIP\"></span>.\n    Would you like to steal control by force or just leave it under the other controller's command?</div>\n    <div style=\"text-align:right\">\n      <button class=\"blueButton\" dojoType=\"dijit.form.Button\" dojoAttachEvent=\"onClick:stealPool\">Steal Control</button>\n      <button dojoType=\"dijit.form.Button\" dojoAttachEvent=\"onClick:hide\">Leave it</button>\n    </div>\n  </div>\n</div>\n",

        postMixInProperties: function() {
            this.inherited(arguments);
            this.popup = new nox.ext.apps.vmanui.BoundedDialog({});
            dojo.connect(this.popup, "onCancel", this, "onCancel");
            this.popup.attr('title', 'Establishing XenServer Connection...');

            for (var i = 0; i < this.pool_obj.properties.length; i++) {
                 var p = this.pool_obj.properties[i];
                 if (p[0] == "master_address_1") {
                     this.master_addr = p[1];
                 }
            }
        },

        postCreate: function() {
            this.pollingDiv  = dojo.query("div.pollingDiv",  this.domNode)[0];
            this.stealingDiv = dojo.query("div.stealingDiv", this.domNode)[0];

            this.pollingDiv.style.display   = 'block';
            this.stealingDiv.style.display  = 'none';
        },

        show: function() {
            this.popup.attr("content", this.domNode);
            dojo.addClass(this.popup.domNode,"poolConnectStatusDialog");
            nox.ext.apps.vmanui.main.onDialogOpen();
            this.popup.show();
            // wait a bit before polling so that the status is updated
            setTimeout(dojo.hitch(this,"poll"), 1000);
        },

        stealPool: function() {
            this.hide();
            nox.ext.apps.vmanui.vac_status.retry_add_pool_with_steal();
        },

        askToSteal: function(oItem) {
            this.pollingDiv.style.display   = 'none';
            this.stealingDiv.style.display  = 'block';

            dojo.query("span.currentControllerIP", this.domNode).forEach( function(oNode) {
                oNode.innerHTML = oItem.controller;
            });
        },

        poll: function() {
            var vmanui = nox.ext.apps.vmanui;
            if (this.canceled) {
              return;
            }
            var d = vmanui.vac_status.get_pool_tree_item(this.pool_obj.uid);
            d.addCallback(
                dojo.hitch(this, function(tree_item) {
                    var status = tree_item["active"];
                    var iStatus = parseInt(status);
                    if (this.canceled) {
                        return;
                    }

                    if ( iStatus == 10 ) {
                        // Only prompt to steal if this not a refresh operation
                        if ( ! this.pool_obj["refresh_status"] ) {
                            this.askToSteal(tree_item);
                        } else {
                            this.hide();
                        }
                        return;
                    } else if ( (iStatus == 0) || (iStatus == 9) ) {
                        // successfully connected or Invalid Pool License
                        this.hide();

                        // This seems to make firebug go nuts, so let's avoid it for now.
                        //nox.ext.apps.vmanui.vac.show_vac_tab("status", tree_item["tree_uid"]);
                        return;
                    } else if (iStatus == 1) {
                        // still trying to reconnect
                        setTimeout(dojo.hitch(this, function() {
                            this.poll();
                        }), 500);
                        return;
                    }
                    // we've had a permanent error
                    this.hide();

                    // At this point, the "Modify Pool" form will be popped up,
                    // so this is no longer a 'Refresh Status" operation, and
                    // we need to delete that property
                    delete this.pool_obj["refresh_status"];

                    var edit_dialog = new vmanui.dialog.pool_edit({
                        pool_obj    : this.pool_obj,
                        error_str   : this._getErrorStr(status)
                    });
                    edit_dialog.show();
                })
            );

            d.addErrback(dojo.hitch(this,function(response) {
                if (response.status == 404) {
                    setTimeout(dojo.hitch(this, function() {
                        this.poll();
                    }), 1000);
                }
            }));
        },

        // destroy the popup widget and our own widget.
        hide: function() {
            nox.ext.apps.vmanui.main.onDialogClose();
            this.popup.destroy();
            this.destroy();
        },

        onCancel : function() {
            this.canceled = true;
            this.hide();
            var edit_dialog = new nox.ext.apps.vmanui.dialog.pool_edit({
                pool_obj : this.pool_obj
            });
            edit_dialog.show();
        },

        _getErrorStr : function(status) {
            switch (status) {
                case 2: return "Internal Error";
                case 3: return "Unable to establish network connection";
                case 4: return "Invalid address";
                case 5: return "Peer is not a valid XenServer";
                case 6: return "Duplicate pool";
                case 7: return "Invalid username or password";
                default: return "Unknown Error";
            }
        }
    }
);


}

